mapper.sql:
where 1=1 

 and u.id = #{record.id,jdbcType=INTEGER}


 and u.username = #{record.username,jdbcType=VARCHAR}


 and u.password = #{record.password,jdbcType=VARCHAR}


 and u.salt = #{record.salt,jdbcType=VARCHAR}


 and u.create_time = #{record.createTime,jdbcType=TIMESTAMP}


 and u.modify_time = #{record.modifyTime,jdbcType=TIMESTAMP}


 and u.deleted = #{record.deleted,jdbcType=INTEGER}

mapper.select:
select 

 from user u


  order by ${order.colName}

    desc



  limit #{count} offset #{offset}
