mapper.sql:
where 1=1 

 and uil.id = #{record.id,jdbcType=BIGINT}


 and uil.biz_tag = #{record.bizTag,jdbcType=VARCHAR}


 and uil.max_id = #{record.maxId,jdbcType=BIGINT}


 and uil.desc = #{record.desc,jdbcType=VARCHAR}


 and uil.create_time = #{record.createTime,jdbcType=TIMESTAMP}


 and uil.modify_time = #{record.modifyTime,jdbcType=TIMESTAMP}

mapper.insert:
insert into unique_id_log (id, biz_tag, max_id, 
  desc, create_time, modify_time
  )
values (#{id,jdbcType=BIGINT}, #{bizTag,jdbcType=VARCHAR}, #{maxId,jdbcType=BIGINT}, 
  #{desc,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

 from unique_id_log uil


  order by ${order.colName}

    desc



  limit #{count} offset #{offset}
